import {WebListsContext} from "../contexts/webLists.context"
import { Body1 } from "../components/Texts";


const Tab = props => {
  return (
    <div>
      <Body1>{props.label}</Body1>
    </div>
  )
}

const Tabs = props => {

  const tabs = useContext(WebListsContext);
  const {webLists} = tabs;
  console.log(tabs)
  return (
    <div>
    {webLists.map(webList => <Tab label={webList.listName}/> )}

    </div>
  )
}

export default Tabs;
