import React, { useContext } from 'react';
import {makeStyles} from "@material-ui/core/styles";
import {WebListsContext} from "../contexts/webLists.context"
import { Body1 } from "../components/Texts";

const useStyles = makeStyles({
  tab_root:{
    padding:"16px",
    marginRight:"16px",
    background:props => props.isActive ? "yellow" : "white",

    "&:hover":{
      background:"grey",
      cursor:"pointer",
    }
  }
});

const Tab = props => {
  const classes = useStyles(props);

  const tabs = useContext(WebListsContext);
  const {findActiveId} = tabs;

  const onClickHandler = (id) => {
    findActiveId(id);
  }

  return (
    <div className={classes.tab_root} onClick={onClickHandler}>
      <Body1>{props.label}</Body1>
    </div>
  )
}

const Tabs = props => {

  const tabs = useContext(WebListsContext);
  const {webLists, activeId} = tabs;
  console.log(tabs)
  return (
    <>
    <div style={{display:"flex"}}>
        {webLists.map((webList,index) => <Tab key={index} label={webList.listName}/> )}
    </div>

    <p>Active Id: {activeId}</p>
    </>
  )
}

export default Tabs;
